rray3 = getArray();\r\nsomeArray3.splice(someArray3.findIndex(v => v.name === \"Kristian\"), 1);\r\nsomeArray3.splice(someArray3.findIndex(v => v.name === \"John\"), 1);\r\nlog(\"\",\r\n  `someArray3.splice(someArray3.findIndex(v => v.name === \"Kristian\"), 1),`,\r\n  `destructive splice /w findIndex Brian remains [someArray3] =`, \r\n  format(someArray3));\r\nlog(`**someArray3.length after splice ${someArray3.length}`);\r\n\r\n// if you\'re not sure about the contents of your array, \r\n// you should check the results of findIndex first\r\nlet someArray4 = getArray();\r\nconst indx = someArray4.findIndex(v => v.name === \"Michael\");\r\nsomeArray4.splice(indx, indx >= 0 ? 1 : 0);\r\nlog(\"\", `someArray4.splice(indx, indx >= 0 ? 1 : 0)`,\r\n  `check findIndex result first [someArray4] = (nothing is removed)`,\r\n  format(someArray4));\r\nlog(`**someArray4.length (should still be 3) ${someArray4.length}`);\r\n\r\n// -- helpers -- \r\nfunction format(obj) {\r\n  return JSON.stringify(obj, null, \" \");\r\n}\r\n\r\nfunction log(...txt) {\r\n  document.querySelector(\"pre\").textContent += `${txt.join(\"\\n\")}\\n`\r\n}\r\n\r\nfunction getArray() {\r\n  return [ {name: \"Kristian\", lines: \"2,5,10\"},\r\n           {name: \"John\", lines: \"1,19,26,96\"},\r\n           {name: \"Brian\", lines: \"3,9,62,36\"} ];\r\n}\r\n\r\n<pre>\r\n**Results**\r\n\r\n</pre>\r\n\r\nExpand snippet\r\n\r\nShare\r\nFollow\r\nedited Aug 23 at 8:51\r\nanswered Apr 5, 2012 at 8:11\r\nKooiInc\'s user avatar\r\nKooiInc\r\n114k3131 gold badges140140 silver badges176176 bronze badges\r\n\r\n    2\r\n    @Klemzy didnt you mean not by index ? by value ...? – \r\n    Royi Namir\r\n    Apr 5, 2012 at 8:24\r\n    455\r\n    The original question asked how to remove the object with the name=\"Kristian\" from the array. Your answer assumes it\'s the first item in the array but what if Kristin is not in the first item? Then your answer doesn\'t work. – \r\n    Rochelle C\r\n    Oct 17, 2012 at 15:29 \r